sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   //Type appropriate comment here, and begin script below
   if(newValue === '') {
		g_form.clearValue('u_emp_id');
	}

	var gaEmpID = new GlideAjax('getEmployeeID');
	gaEmpID.addParam('sysparm_name', 'get_employeeID');
	gaEmpID.addParam('sysparm_user', g_form.getValue('u_employee_name'));
	gaEmpID.getXMLAnswer(populateEmployeeIDField);

	function populateEmployeeIDField(response) {
		var empIDFromScript = response.responseXML.documentElement.getAttribute("answer");
			g_form.clearValue('employee_number');
			g_form.setValue('u_emp_id', populateEmployeeIDField);
	}

}
